PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE questions (
	id INTEGER NOT NULL, 
	text VARCHAR, 
	image_url VARCHAR, 
	explanation VARCHAR, "title" VARCHAR, "type" VARCHAR, "image_explanation_url" VARCHAR, "takeaway" VARCHAR, 
	PRIMARY KEY (id)
);
CREATE TABLE choices (
	id INTEGER NOT NULL, 
	text VARCHAR, 
	is_correct BOOLEAN, 
	question_id INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(question_id) REFERENCES questions (id)
);
CREATE TABLE responses (
	id INTEGER NOT NULL, 
	question_id INTEGER, 
	choice_id INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(question_id) REFERENCES questions (id), 
	FOREIGN KEY(choice_id) REFERENCES choices (id)
);
CREATE INDEX ix_questions_text ON questions (text);
CREATE INDEX ix_questions_id ON questions (id);
CREATE INDEX ix_choices_text ON choices (text);
CREATE INDEX ix_choices_id ON choices (id);
CREATE INDEX ix_responses_id ON responses (id);
COMMIT;
